import React, {Fragment} from 'react'

const inputContainer = ({i}) => (
     <div>
        <input type="radio" name="" id={i}/>
        <label for = {i}>{i}</label>
    </div>
)
const SizeChart = () => {
    let links = []
    for(let i = 5; i <= 12; i++) {
        links.push(
        <Fragment>
            <inputContainer i = {i} />
            <inputContainer i = {`${i}.5`}/>
        </Fragment>
        )
    }
    return (
        <div className = 'size-chart'>
            <h1>HELLO</h1>
            {links}
        </div>
    )
}

export default SizeChart
