import styled from 'styled-components'

export const ScrollingWrapperContainer = styled.div`
padding: 50px 0 50px 0px;
max-width: 100vw;
overflow-x: hidden;

` 
export const ScrollingWrapper = styled.div`
    position: relative;
`

export const ScrollingContent = styled.div`
    display: grid;
    grid-template-columns: repeat(12, 31%);
    -webkit-overflow-scrolling: touch;
    overflow-x: hidden;
    overflow-y: hidden;
    gap: 20px;
    scrollbar-width: none;
    transition: all 1s ease;

    &::-webkit-scrollbar {
    display: none
    }
`

export const WrapperItem = styled.div`    
    img {
        width: 100%;
        height: 100%;
        object-fit: cover;
    }
`

export const Arrow = styled.div`
    position: absolute;
    top: 0px;
    bottom: 0;
    width: 65px;
    opacity: 1;
    z-index: 100;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all .5s ease-in-out;
    right: ${props => props.isRight ? '70px' : ''};

    .fas { 
        font-size: 25px;
        color: rgba(109, 109, 109, .6);
        background: rgba(255, 255, 255, .3);
        height: 50px;
        width: 50px;
        border-radius: 100%;
        text-align: center;
        display: flex;
        justify-content: center;
        align-items: center;
        transition: all .3s ease;
    }
`

export const ProgressBarContainer = styled.div`
    width: 95%;
    margin-top: 50px;
`

export const Scrollbar = styled.div`
    width: 100%;
    margin: auto;
    height: 2px;
    background: rgba(169, 169, 169, .7);
    border-radius: 100px;
    overflow: hidden;
`

export const ProgressBar = styled.div`
    background: rgba(55, 55, 55, .8);
    height: 100%;
    width: 30%;
    border-radius: 100px;
    transition: all .5s ease;
    margin-left: ${props => `${props.scrollProgress}%`};
    transform: ${props => `translateX: -${props.scrollProgress}%`}
`
