import React, {useState, useEffect} from 'react'
import {animated, useSpring, useTransition, config} from 'react-spring'

import {connect} from 'react-redux'
import {selectCollection, selectCollectionByFilter} from '../../redux/shop/shop.selectors'

import CollectionItem from '../../components/collection-item/collection-item.component'
import FilterMenu from '../../components/filter-menu/filter-menu.component'

import './collection.styles.scss'

const CollectionPage = ({ collection, filteredCollection }) => {
    const [value, setValue] = useState('');

    const [isOpen, setIsOpen] = useState(true)    
    const {title, items} = collection

    const tags = items.map(el => el.tags).flat(1);
    const tagsFlatMap = [...new Set(tags)]

    // export const fetchCollectionByFilter = (event, items) => {
    //     const value = event.target.textContent
    //     let list= [];
    //     items.map(item => {
    //         if(item.tags.includes(value)) {
    //             list.push(item)
    //         }
    //         return list
    //     })  
    // }

    useEffect(() => {
        const list = items.filter(item => item.tags.includes('walking'))
        console.log(list)
    }, [])

    return (
    <div className="collection-page">
        <div className="collection-page-header">
            <h2 className="title">{title}</h2>
            <div className="filter-options"  onClick = {() => setIsOpen(!isOpen)}>
                <div className = 'filter-btn'>
                    {isOpen ? 'Show' : 'Hide'} Filters
                    <span></span>
                </div>
                <div className = 'sort-btn'>
                    Sort By <i class="fas fa-chevron-down"></i>
                </div>
            </div>
        </div>
        <div className="collection-page-container">
            <FilterMenu items = {tagsFlatMap} isVisible = {isOpen} handleClick = {(event) => filteredCollection(event, collection)}/>
            <div  className="items">
                {items.map(item => 
                    <CollectionItem key = {item.id} item = {item}/>
                )}
            </div>
        </div>
    </div>
)}

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
})

const mapDispatchToProps = dispatch => ({
    collection: event => dispatch(selectCollectionByFilter(event)),
});

export default connect(mapStateToProps)(CollectionPage)
