import React, {useState, useEffect} from 'react'
import {withRouter} from 'react-router-dom'

import CollectionItem from '../../components/collection-item/collection-item.component'
import FilterMenu from '../../components/filter-menu/filter-menu.component'

import CollectionsOverview from '../../components/collections-overview/collections-overview.component'
import CollectionPreview from '../../components/preview-collection/preview-collection.component'
import {CollectionItemContainer, CollectionPageContainer, CollectionContainer} from './collection-container.styles'

import FilterHeader from '../../components/filter-header/filter-header.component'
import { createStructuredSelector } from 'reselect'
import { selectFilterMenu } from '../../redux/shop/shop.selectors'
import { toggleFillterMenu } from '../../redux/shop/shop.actions'

const CollectionsContainer = ({ items, title, tags, hidden, children, history, ...rest }) => {
    const [isOpen, setIsOpen] = useState(true)    

    const handleClick = event => {
        const value = event.target.textContent;
        history.push(`/shop/filter/${value}`)
    }

    return (
    <CollectionContainer>
        <FilterHeader title = {title} isOpen = {isOpen} setIsOpen = {setIsOpen} {...rest}/>
        <CollectionPageContainer>
            <FilterMenu items = {tags} isVisible = {isOpen} handleClick = {handleClick}/>
            <CollectionItemContainer isOpen = {isOpen}>
               {children}
            </CollectionItemContainer>
        </CollectionPageContainer>
    </CollectionContainer>
)}

const mapDispatchToProps = dispatch => ({
    toggleFilterMenu: () => dispatch(toggleFillterMenu())
})

const mapStateToProps = createStructuredSelector({
    hidden: selectFilterMenu
})

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(CollectionsContainer))
